// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

/**
 * @title LoanContract
 * @dev Manages an individual, automated loan agreement.
 * A new instance of this contract will be deployed for each loan.
 * It will handle fund disbursement, repayment schedules, and interest calculations.
 */
contract LoanContract {
    // State variables for a loan
    address public borrower;
    address public lender;
    address public stablecoin;
    uint256 public principal;
    uint256 public interestRate;
    uint256 public term;
    uint256 public creationDate;

    enum LoanStatus { Active, Repaid, Defaulted }
    LoanStatus public status;

    constructor(
        address _borrower,
        address _lender,
        address _stablecoin,
        uint256 _principal,
        uint256 _interestRate,
        uint256 _term
    ) {
        borrower = _borrower;
        lender = _lender;
        stablecoin = _stablecoin;
        principal = _principal;
        interestRate = _interestRate;
        term = _term;
        creationDate = block.timestamp;
        status = LoanStatus.Active;
    }

    // We will add functions here for:
    // - Disbursing the loan to the borrower.
    // - Allowing the borrower to make repayments.
    // - Handling defaults.
    // - Marking the loan as fully repaid.
}

