pragma solidity ^0.8.20;

/**
 * @title Reputation
 * @dev Manages the "Morph Credit Score" for each user.
 * This contract will calculate, store, and update a reputation score
 * based on a user's on-chain activities.
 */
contract Reputation {
    // Mapping from a user's address to their Morph Credit Score
    mapping(address => uint256) public morphCreditScores;

    // Event to be emitted when a score is updated
    event ScoreUpdated(address indexed user, uint256 newScore);

    /**
     * @dev Updates the credit score for a given user.
     * For now, this will be a simple function. In the future, it will be called
     * by other contracts or an authorized oracle based on complex logic.
     * @param user The address of the user.
     * @param newScore The new credit score.
     */
    function updateScore(address user, uint256 newScore) external {
        // Add access control here later to ensure only authorized callers can update scores.
        morphCreditScores[user] = newScore;
        emit ScoreUpdated(user, newScore);
    }

    /**
     * @dev Retrieves the credit score for a given user.
     * @param user The address of the user.
     * @return The user's current Morph Credit Score.
     */
    function getScore(address user) external view returns (uint256) {
        return morphCreditScores[user];
    }
}

