// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Reputation.sol";

/**
 * @title LoanContract
 * @dev Manages an individual, automated loan agreement.
 */
contract LoanContract {
    // State variables
    address public borrower;
    address public lender;
    IERC20 public stablecoin;
    Reputation public reputationContract;
    uint256 public principal;
    uint256 public interestRate; // in basis points
    uint256 public term; // in seconds
    uint256 public creationDate;
    uint256 public amountRepaid;
    bool public disbursed;

    enum LoanStatus { Active, Repaid, Defaulted }
    LoanStatus public status;

    event Disbursed(address indexed borrower, uint256 amount);
    event Repaid(address indexed borrower, uint256 amount);
    event DefaultClaimed(address indexed lender);

    constructor(
        address _borrower,
        address _lender,
        address _stablecoinAddress,
        address _reputationAddress,
        uint256 _principal,
        uint256 _interestRate,
        uint256 _term
    ) {
        borrower = _borrower;
        lender = _lender;
        stablecoin = IERC20(_stablecoinAddress);
        reputationContract = Reputation(_reputationAddress);
        principal = _principal;
        interestRate = _interestRate;
        term = _term;
        creationDate = block.timestamp;
        status = LoanStatus.Active;
    }

    /**
     * @dev Allows the borrower to withdraw the loan principal.
     */
    function disburse() external {
        require(msg.sender == borrower, "Only borrower can disburse");
        require(!disbursed, "Loan already disbursed");
        disbursed = true;
        require(stablecoin.transfer(borrower, principal), "Disbursement failed");
        emit Disbursed(borrower, principal);
    }

    /**
     * @dev Allows the borrower to repay the loan.
     */
    function repay() external {
        require(msg.sender == borrower, "Only borrower can repay");
        require(status == LoanStatus.Active, "Loan is not active");

        uint256 totalOwed = principal + (principal * interestRate) / 10000;
        uint256 remainingOwed = totalOwed - amountRepaid;

        require(stablecoin.transferFrom(msg.sender, address(this), remainingOwed), "Repayment transfer failed");
        amountRepaid += remainingOwed;

        if (amountRepaid >= totalOwed) {
            status = LoanStatus.Repaid;
            reputationContract.rewardRepayment(borrower);
            require(stablecoin.transfer(lender, totalOwed), "Lender payment failed");
            emit Repaid(borrower, totalOwed);
        }
    }

    /**
     * @dev Allows the lender to claim a default if the loan is past due.
     */
    function claimDefault() external {
        require(msg.sender == lender, "Only lender can claim default");
        require(status == LoanStatus.Active, "Loan is not active");
        require(block.timestamp > creationDate + term, "Loan is not past due");

        status = LoanStatus.Defaulted;
        reputationContract.penalizeDefault(borrower);
        // In a real scenario, you might transfer remaining collateral or enact other penalties.
        // For now, we just update the status.
        emit DefaultClaimed(lender);
    }
}

contracts/LoanMarketplace.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Reputation.sol";
import "./LoanContract.sol";

/**
 * @title LoanMarketplace
 * @dev Facilitates the P2P lending marketplace.
 */
contract LoanMarketplace {
    IERC20 public stablecoin;
    Reputation public reputationContract;

    struct LoanRequest {
        uint256 id;
        address borrower;
        uint256 amount;
        uint256 term; // in seconds
        uint256 interestRate; // basis points (e.g., 500 = 5%)
        bool fulfilled;
        address loanContractAddress;
    }

    mapping(address => uint256) public lenderBalances;
    mapping(uint256 => LoanRequest) public loanRequests;
    uint256 public nextLoanRequestId;

    event FundsDeposited(address indexed lender, uint256 amount);
    event FundsWithdrawn(address indexed lender, uint256 amount);
    event LoanRequested(uint256 indexed requestId, address indexed borrower, uint256 amount, uint256 term, uint256 interestRate);
    event LoanFunded(uint256 indexed requestId, address indexed lender, address loanContract);

    constructor(address _stablecoinAddress, address _reputationAddress) {
        stablecoin = IERC20(_stablecoinAddress);
        reputationContract = Reputation(_reputationAddress);
    }

    /**
     * @dev Allows lenders to deposit stablecoins into the marketplace.
     * @param amount The amount of stablecoins to deposit.
     */
    function deposit(uint256 amount) external {
        require(amount > 0, "Deposit amount must be positive");
        lenderBalances[msg.sender] += amount;
        require(stablecoin.transferFrom(msg.sender, address(this), amount), "Deposit failed");
        emit FundsDeposited(msg.sender, amount);
    }

    /**
     * @dev Allows lenders to withdraw their deposited stablecoins.
     * @param amount The amount to withdraw.
     */
    function withdraw(uint256 amount) external {
        require(amount > 0, "Withdraw amount must be positive");
        require(lenderBalances[msg.sender] >= amount, "Insufficient balance");

        lenderBalances[msg.sender] -= amount;
        require(stablecoin.transfer(msg.sender, amount), "Withdrawal failed");

        emit FundsWithdrawn(msg.sender, amount);
    }

    /**
     * @dev Allows borrowers to request a new loan.
     * @param amount The amount of the loan requested.
     * @param term The duration of the loan in seconds.
     * @param interestRate The proposed interest rate in basis points.
     */
    function requestLoan(uint256 amount, uint256 term, uint256 interestRate) external {
        require(amount > 0, "Loan amount must be positive");
        uint256 creditScore = reputationContract.getScore(msg.sender);
        require(creditScore > 500, "Insufficient credit score"); // Example requirement

        uint256 requestId = nextLoanRequestId++;
        loanRequests[requestId] = LoanRequest({
            id: requestId,
            borrower: msg.sender,
            amount: amount,
            term: term,
            interestRate: interestRate,
            fulfilled: false,
            loanContractAddress: address(0)
        });

        emit LoanRequested(requestId, msg.sender, amount, term, interestRate);
    }

    /**
     * @dev Allows a lender to fund an existing loan request.
     * @param requestId The ID of the loan request to fund.
     */
    function fundLoan(uint256 requestId) external {
        LoanRequest storage request = loanRequests[requestId];
        require(request.id == requestId, "Loan request does not exist");
        require(!request.fulfilled, "Loan already fulfilled");
        require(lenderBalances[msg.sender] >= request.amount, "Insufficient balance");

        // Decrease lender's balance
        lenderBalances[msg.sender] -= request.amount;

        // Create and fund the new loan contract
        LoanContract newLoan = new LoanContract(
            request.borrower,
            msg.sender,
            address(stablecoin),
            address(reputationContract),
            request.amount,
            request.interestRate,
            request.term
        );

        address newLoanAddress = address(newLoan);
        request.loanContractAddress = newLoanAddress;

        // Transfer the principal to the new loan contract
        require(stablecoin.transfer(newLoanAddress, request.amount), "Funding failed");

        // Mark the request as fulfilled
        request.fulfilled = true;

        emit LoanFunded(requestId, msg.sender, newLoanAddress);
    }
}

contracts/Reputation.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

/**
 * @title Reputation
 * @dev Manages the "Morph Credit Score" for each user.
 * This contract will calculate, store, and update a reputation score
 * based on a user's on-chain activities.
 */
contract Reputation {
    // Constants for score changes
    uint256 private constant REPAYMENT_REWARD = 25;
    uint256 private constant DEFAULT_PENALTY = 50;

    // Mapping from user address to their Morph Credit Score
    mapping(address => uint256) public morphCreditScores;

    event ScoreUpdated(address indexed user, uint256 newScore);

    /**
     * @dev Initializes a user with a base score.
     * Can be called by an admin or a factory contract.
     */
    function initializeUser(address user, uint256 initialScore) public {
        require(morphCreditScores[user] == 0, "User already initialized");
        morphCreditScores[user] = initialScore;
        emit ScoreUpdated(user, initialScore);
    }

    /**
     * @dev Increases a user's score for a successful repayment.
     * Should only be callable by a LoanContract.
     */
    function rewardRepayment(address user) external {
        uint256 currentScore = morphCreditScores[user];
        uint256 newScore = currentScore + REPAYMENT_REWARD;
        morphCreditScores[user] = newScore;
        emit ScoreUpdated(user, newScore);
    }

    /**
     * @dev Decreases a user's score for a default.
     * Should only be callable by a LoanContract.
     */
    function penalizeDefault(address user) external {
        uint256 currentScore = morphCreditScores[user];
        // Prevent score from going below zero
        uint256 newScore = currentScore > DEFAULT_PENALTY ? currentScore - DEFAULT_PENALTY : 0;
        morphCreditScores[user] = newScore;
        emit ScoreUpdated(user, newScore);
    }

    /**
     * @dev Retrieves the Morph Credit Score for a user.
     */
    function getScore(address user) public view returns (uint256) {
        return morphCreditScores[user];
    }
}

